class FoodRatings {

    class FoodRating {
        String food;
        int rating;
        FoodRating(String food, int rating){
            this.food = food;
            this.rating = rating;
        }
    }

    Map<String, String> foodCuisineMap = new HashMap<>();
    Map<String, FoodRating> foodRatingMap = new HashMap<>();
    Map<String, PriorityQueue<FoodRating>> cuisineFoodRatingMap 
        = new HashMap<>();

    public FoodRatings(String[] foods, String[] cuisines, int[] ratings) {
        foodRatingMap = new HashMap<>();
        cuisineFoodRatingMap = new HashMap<>();
        foodCuisineMap = new HashMap<>();

        int n = foods.length;
        for(int i=0;i<n;i++){
            foodCuisineMap.put(foods[i], cuisines[i]);
            FoodRating fr = new FoodRating(foods[i], ratings[i]);
            foodRatingMap.put(foods[i], fr);
            PriorityQueue<FoodRating> pq = cuisineFoodRatingMap.getOrDefault(cuisines[i], new PriorityQueue<FoodRating>((p1, p2) -> (p1.rating==p2.rating) ? p1.food.compareTo(p2.food) : p2.rating-p1.rating));
            pq.offer(fr);
            cuisineFoodRatingMap.put(cuisines[i], pq);
        }
    }

    public void changeRating(String food, int newRating) {

        FoodRating foodRatingInstance = foodRatingMap.get(food);
        String cuisine = foodCuisineMap.get(food);

        PriorityQueue<FoodRating> pq = cuisineFoodRatingMap.get(cuisine);
        pq.remove(foodRatingInstance);

        FoodRating newInstance = new FoodRating(food, newRating);
        pq.offer(newInstance); 

        cuisineFoodRatingMap.put(food, pq);
        foodRatingMap.put(food, newInstance);
    }

    public String highestRated(String cuisine) {
        return cuisineFoodRatingMap.get(cuisine).peek().food;
    }
}

