class FoodPQ
{
    vector<pair<string, int>> v;
    unordered_map<string, int> mp;	// food to idx_in_heap
    public:
        FoodPQ() {}
    void push(pair<string, int> p)
    {
        v.push_back(p);
        mp[p.first] = v.size()-1;
    }
    bool isLeftBigger(pair<string, int> a, pair<string, int> b)
    {
        if (a.second != b.second) return a.second > b.second;
        return a.first < b.first;
    }
    void swapper(int idx1, int idx2)
    {
        mp[v[idx1].first] = idx2;
        mp[v[idx2].first] = idx1;
        swap(v[idx1], v[idx2]);
    }
    bool isV(int idx){
        return idx>=0 && idx<v.size();
    }
    void bubbleUp(int idx)
    {
       	// i -> 2i+1, 2i+2
       	// child-1/2 = parent
        int par = (idx - 1) / 2;
        if (isV(par) && isLeftBigger(v[idx], v[par]))
        {
            swapper(idx, par);
            bubbleUp(par);
        }

    }
    void bubbleDn(int idx)
    {
       	// i -> 2i+1, 2i+2
       	// child-1/2 = parent
        int c1 = 2 *idx + 1;
        int c2 = 2 *idx + 2;
        int n = v.size();

        if (isV(c1)==false || isLeftBigger(v[idx], v[c1]))  // cant go c1 side
        {
           	// can only go c2 side
            if (isV(c2) && (!isLeftBigger(v[idx], v[c2])))
            {
                swapper(idx, c2);
                bubbleDn(c2);
            }
        }
        else
        {
           	// can go c1 side
            if ( isV(c2)==false || isLeftBigger(v[idx], v[c2]))	// cant go c2 side 
            {
                swapper(idx, c1);
                bubbleDn(c1);
            }
            else
            {
               	// might go either c1/c2
                if ( isV(c2) && isLeftBigger(v[c1], v[c2]) )
                {
                   	// go down c1 side 
                    swapper(idx, c1);
                    bubbleDn(c1);
                }
                else
                {
                   	// go c2 side
                    swapper(idx, c2);
                    bubbleDn(c2);
                }
            }
        }
    }
    void heapify()
    {
        for(int i = v.size()-1;i>=0;i--){
            bubbleDn(i);
        }
    }
    void update(string food, int r)
    {
        int idx = mp[food];
        int orgR = v[idx].second;
        v[idx].second = r;
        if(r>orgR)
        bubbleUp(idx);
        else bubbleDn(idx);
    }
    string getMax()
    {
        return v[0].first;
    }
};
