struct compare {
    bool operator() (const pair<int, string>& a, const pair<int, string>& b) const{
        if ( a.first > b.first ) return true;
        else if ( a.first == b.first ) return a.second < b.second ? true : false;
        return false;
    }
};

class FoodRatings {
    unordered_map<string, pair<int, string>> m; // keep track of the rating and cuisine of each food
    unordered_map<string, set<pair<int, string>, compare>> c; // keep track of an ordered set for each cuisine
public:
    FoodRatings(vector<string>& foods, vector<string>& cuisines, vector<int>& ratings) {
        for ( int i = 0; i < foods.size(); ++i )
        {
            m[foods[i]] = { ratings[i], cuisines[i] };
            c[cuisines[i]].insert({ratings[i], foods[i]});
        }
    }

    void changeRating(string food, int newRating) {
        if ( m.find(food) == m.end() ) return;

        auto [oldRating, cuisine] = m.find(food)->second; // get the rating and cuisine for this food
        m[food].first = newRating;

        // Let's update the rating in the cuisine map
        c.find(cuisine)->second.erase(c.find(cuisine)->second.find({oldRating, food}));
        c.find(cuisine)->second.insert({newRating, food});
    }

    string highestRated(string cuisine) {
        return c.find(cuisine)->second.begin()->second;
    }
};

