class FoodRatings {

   class Node {
        Map<String, Integer> foodRatingMap;
        Map<Integer, TreeSet<String>> ratingFoodMap;

        public Node(){
            foodRatingMap = new HashMap<>();
            ratingFoodMap = new TreeMap<>(Comparator.reverseOrder());
        }
    }

    private Map<String, String> foodCusineMap;
    private Map<String, Node> cusineFoodRatingMap;

    public FoodRatings(String[] foods, String[] cuisines, int[] ratings) {
        foodCusineMap = new HashMap<>();
        cusineFoodRatingMap = new HashMap<>();
        for (int i = 0; i < foods.length; i++){
            String food = foods[i];
            String cusine = cuisines[i];
            int rating = ratings[i];
            foodCusineMap.put(food, cusine);

            if(cusineFoodRatingMap.get(cusine) == null){
                cusineFoodRatingMap.put(cusine, new Node());
            }

            Map<String, Integer> foodRatingMap = cusineFoodRatingMap.get(cusine).foodRatingMap;
            Map<Integer, TreeSet<String>> ratingFoodMap = cusineFoodRatingMap.get(cusine).ratingFoodMap;

            foodRatingMap.put(food, rating);
            TreeSet<String> set = ratingFoodMap.getOrDefault(rating, new TreeSet<>());
            set.add(food);
            ratingFoodMap.put(rating, set);
        }
    }

    public void changeRating(String food, int newRating) {
        String cusine = foodCusineMap.get(food);

        Map<String, Integer> foodRatingMap = cusineFoodRatingMap.get(cusine).foodRatingMap;
        Map<Integer, TreeSet<String>> ratingFoodMap = cusineFoodRatingMap.get(cusine).ratingFoodMap;

        int currRating = foodRatingMap.get(food);
        //remove the food from the current rating map
        TreeSet<String> set = ratingFoodMap.getOrDefault(currRating, new TreeSet<>());
        set.remove(food);
        if(set.isEmpty()) //caution , to remove the key if the set size if 0
            ratingFoodMap.remove(currRating);
        else 
            ratingFoodMap.put(currRating, set);

        //update the mapping
        foodRatingMap.put(food, newRating);

        //add the food in the current rating 
        TreeSet<String> newFoodSet = ratingFoodMap.getOrDefault(currRating, new TreeSet<>());
        newFoodSet.add(food);
        ratingFoodMap.put(newRating, newFoodSet);
    }

    public String highestRated(String cuisine) {
        Map<Integer, TreeSet<String>> ratingFoodMap = cusineFoodRatingMap.get(cuisine).ratingFoodMap;
        int highestRating = ratingFoodMap.keySet().iterator().next(); //first item in the map
        TreeSet<String> foodSet = ratingFoodMap.get(highestRating);
        return foodSet.iterator().next();
    }
}

