class FoodRatings {
public:
    FoodRatings(vector<string>& foods, vector<string>& cuisines, vector<int>& ratings) {
        const auto sz = foods.size();
        for (int i=0;i<sz;++i) {
            CuisineToFood[cuisines[i]].insert({foods[i], ratings[i]});
            FoodToCuisine[foods[i]] = {ratings[i], cuisines[i]};
        }
    }

    void changeRating(string food, int newRating) {
        auto it = FoodToCuisine.find(food);
        const auto& cuisine = it->second.second;

        FoodRating fr{food, it->second.first};
        CuisineToFood[cuisine].erase(fr);
        CuisineToFood[cuisine].insert({food, newRating});

        FoodToCuisine[food] = {newRating, it->second.second};
    }

    string highestRated(string cuisine) {
        const auto& p = CuisineToFood.find(cuisine);
        return p->second.begin()->food;
    }

