class Food:
    def __init__(self, name: str, cuisine: str, rating: int):
        self.name = name
        self.cuisine =  cuisine
        self.rating = rating

class CuisineRanking:
    def __init__(self) -> None:
        self.food = []

    def add(self, food: Food) -> None:
        heappush(self.food, (-food.rating, food.name, food))

    def get_top(self) -> Food:
        while self.food and self.food[0][0] != -self.food[0][2].rating:
            heappop(self.food)
        if not self.food:
            return None
        return self.food[0][2]

class FoodRatings:

    def __init__(self, foods: List[str], cuisines: List[str], ratings: List[int]):
        self.all_food = defaultdict(Food)
        self.rankers = defaultdict(CuisineRanking)
        for i in range(len(foods)):
            food = Food(foods[i], cuisines[i], ratings[i])
            self.all_food[foods[i]] = food
            self.rankers[cuisines[i]].add(food)

    def changeRating(self, food_name: str, newRating: int) -> None:
        if food_name not in self.all_food:
            return None
        food = self.all_food[food_name]
        food.rating = newRating
        self.rankers[food.cuisine].add(food)

    def highestRated(self, cuisine: str) -> str:
        if cuisine not in self.rankers:
            return "not valid"
        top_food = self.rankers[cuisine].get_top()
        if not top_food:
            return "not valid"
        return top_food.name



