        this.name = name;
        this.cuisine = cuisine;
        this.rating = rating;
    }

    @Override
    public int compareTo(FoodData other) {
        if (this.rating == other.rating) {
            return this.name.compareTo(other.name);
        }
        return other.rating - this.rating;
    }
