        self.ratings = defaultdict(list) # c --> [[-r, f], ...]
        self.frc = {} # f --> r, c

        for f, c, r in zip(foods, cuisines, ratings):
            self.ratings[c].append([-r, f])
            self.frc[f] = r, c

        for h in self.ratings.values():
            heapify(h)

    def changeRating(self, food: str, newRating: int) -> None:
        cuisine = self.frc[food][1]
        heappush(self.ratings[cuisine], [-newRating, food])
        self.frc[food] = (newRating, cuisine)    

    def highestRated(self, cuisine: str) -> str:
        heap = self.ratings[cuisine]
        while self.frc[heap[0][1]][0] != -heap[0][0]:
            heappop(heap)
        return heap[0][1]
