        int curr_rating = food_rating[food];

        food_rating[food] = newRating;

        string find_cusine = food_cousin[food];


        cousin_rating[find_cusine].erase({ -curr_rating,
            food });

        cousin_rating[find_cusine].insert({ -newRating,
