class FoodRatings {
public:
    struct cmp{
        bool operator()(const pair<int,string> &a,const pair<int,string>& b)
        const {
            if(a.first!=b.first)
            {
                return a.first>b.first;
            }
            return a.second<b.second;
        }
    };

    unordered_map<string,set<pair<int,string>,cmp>> m;
    unordered_map<string,string> m2;
    unordered_map<string,pair<int,string>> m3;
    FoodRatings(vector<string>& foods, vector<string>& cuisines, vector<int>& ratings) {
        for(int i=0;i<ratings.size();++i)
        {
            m[cuisines[i]].insert({ratings[i],foods[i]});
            m2[foods[i]]=cuisines[i];
            m3[foods[i]]={ratings[i],foods[i]};
        }
    }

    void changeRating(string food, int newRating) {
        string& cuisi=m2[food];
        pair<int,string>& oldfood=m3[food];
        m[cuisi].erase(oldfood);
        m[cuisi].insert({newRating,food});
        m3[food]={newRating,food};
    }

    string highestRated(string cuisine) {
        return (*(m[cuisine].begin())).second;
    }
};
