class FoodRatings {

    private Map<String, FoodInfo> foodInfos;
    private Map<String, PriorityQueue<FoodInfo>> cuisinesMap;

    public FoodRatings(String[] foods, String[] cuisine, int[] ratings) {
        foodInfos = new HashMap<>();
        cuisinesMap = new HashMap<>();

        for(int i = 0; i < foods.length; i += 1) {
            FoodInfo foodObj = new FoodInfo(foods[i], cuisine[i], ratings[i]);

            cuisinesMap.putIfAbsent(cuisine[i], new PriorityQueue<>((a, b) -> {
                if(a.rating == b.rating) return a.name.compareTo(b.name);
                return Integer.compare(b.rating, a.rating);
            }));

            cuisinesMap.get(cuisine[i]).offer(foodObj);
            foodInfos.put(foods[i], foodObj);
        }
    }

    public void changeRating(String food, int newRating) {
        FoodInfo obj = foodInfos.get(food);
        PriorityQueue<FoodInfo> nameRatings = cuisinesMap.get(obj.cuisine);
        nameRatings.remove(obj); //This step makes this approach slower than TreeSet Approach
        obj.rating = newRating;
        nameRatings.offer(obj);
    }

    public String highestRated(String cuisine) {
        return cuisinesMap.get(cuisine).peek().name;
    }

    private class FoodInfo {
        String name;
        String cuisine;
        int rating;

        FoodInfo(String name, String cuisine, int rating) {
            this.name = name;
            this.cuisine = cuisine;
            this.rating = rating;
        }
    }
}
