  //cuisineId :: Food with highest rating in front
    Map<String,PriorityQueue<Food>> cuisinesMap = new HashMap<>();

    //FoodId::Food reference to enable disable food
    Map<String,Food> refMap = new HashMap<>();

    public FoodRatings(String[] foods, String[] cuisines, int[] ratings) {

        int n = foods.length;
        for(int i=0;i<n;i++){

            Food food = new Food(foods[i],cuisines[i],ratings[i],true);

            refMap.put(foods[i],food);
            cuisinesMap.putIfAbsent(cuisines[i],new PriorityQueue<>());
            cuisinesMap.get(cuisines[i]).offer(food);
        }
    }

    public void changeRating(String food, int newRating) {

        Food foodRef = refMap.get(food);
        if(foodRef!=null){
            foodRef.enabled = false;

            Food newFood = new Food(food,foodRef.cuisineId,newRating,true);
            cuisinesMap.get(foodRef.cuisineId).offer(newFood);
            refMap.put(food,newFood);
        }

    }

    public String highestRated(String cuisine) {

        Food found = null;
        PriorityQueue<Food> queue = cuisinesMap.get(cuisine);

        while(found==null && !queue.isEmpty()){

            Food food = queue.peek();
            if(food.enabled){
                found = food;
            }else{
                queue.poll();
            }
        }


        return found == null ? null : found.foodId;
    }

    private static class Food implements Comparable<Food>{

        public String foodId;
        public String cuisineId;
        public Integer rating;
        public boolean enabled;

