        for(int i:s)
            freq[cnt(i)]++;
        for(int i=1;i<=30;i++){
            for(int j=1;j<=30;j++){
                if(i+j>=k)
                    ans+=freq[i]*freq[j];
            }
        }
        return ans;
    }
    int cnt(int n){
        int c=0;
        while(n){
            n=n&n-1;
            c++;
        }
        return c;
