long long countExcellentPairs(vector<int>& nums, int k) {
        vector<int>arr;
        set<int>st(nums.begin(),nums.end());

        for(auto i=st.begin();i!=st.end();i++){
                auto a = *i;
                int count=0;
                for(int j=0;j<32;j++){
                    if(*i & (1<<j))count++;

                }
            arr.push_back(count);
        }
        long long ans=0;

        sort(arr.begin(),arr.end());

        for (int i = 0; i < arr.size(); ++i) {

        int j = lower_bound(arr.begin(), arr.end(), k-arr[i]) - arr.begin();


        ans += arr.size()-j;
    }

