class Solution {

    // Brian Kernighan’s Algorithm
    private static int countSetBits(int number){
        int count = 0;
        while(number != 0){
            number = (number)&(number-1);
            count++;
        }
        return count;
    }

    private static int[] storeSetBits(int[] nums){
        int[] store = new int[nums.length];
        for(int index=0;index<nums.length;index++){store[index] = countSetBits(nums[index]);}
        return store;
    }

    private static long twoSumAnalogy(int[] nums, int[] store, int target){
        Set<List<Integer>> set = new HashSet<>();
        for(int index1=0,len=store.length;index1<len;index1++){
            for(int index2=0;index2<len;index2++){
                if(store[index1] + store[index2] >= target){
                    set.add(Arrays.asList(nums[index1],nums[index2]));
                }
            }
        }
        return (long)set.size();
    }

    public long countExcellentPairs(int[] nums, int k) {
        int[] store = new int[nums.length];
        store = storeSetBits(nums);
        return twoSumAnalogy(nums,store,k);
    }
}

