class Solution {
public:
    long long countExcellentPairs(vector<int>& a, int k) {
        int n = a.size();
        vector<long long> cnt;
        set<int> s;
        for(int i = 0; i < n; i++) s.insert(a[i]);
        for(auto i = s.begin(); i != s.end(); i++) {
            cnt.push_back(__builtin_popcount(*i));
        }
        sort(cnt.begin(), cnt.end(), greater<int>());
        stack<int> st;
        long long ans = 0;
        for(int i = 0; i < cnt.size(); i++) {
            while(!st.empty() && cnt[st.top()] + cnt[i] < k) st.pop();
            if(!st.empty()) ans += (1 + st.top()) * 2LL;
            if(2 * cnt[i] >= k) ans++;
            st.push(i);
        }
        return ans;
    }
};
