```
class Solution {
public:

    // function for counting no. of set bits in num

    int find_set_bits(int num)
    {
        int count = 0;

        while(num)
        {
            count++;

            num = (num & (num - 1));
        }

        return count;
    }

    long long countExcellentPairs(vector<int>& nums, int k) {

        int n = nums.size();

        sort(nums.begin(), nums.end());

        // make unique array, to avoid duplicates pairs

        vector<int> arr;

        int i = 0;

        while(i < n)
        {
            while(i < n - 1 && nums[i] == nums[i + 1])
            {
                i++;
            }

            arr.push_back(nums[i]);

            i++;
        }

        // set_bit array will store the no. of set bits presents in arr[i]

        vector<int> set_bit;

        for(int i = 0; i < arr.size(); i++)
        {
            int count = find_set_bits(arr[i]);

            set_bit.push_back(count);
        }

        // sort the set_bit array

        sort(set_bit.begin(), set_bit.end());

        // find all the unique pairs

        long long count = 0;

        for(int i = 0; i < arr.size(); i++)
        {
            // no. of minimum set bits required, so that sum can be greater than k

            int need = k - set_bit[i];

            // upply lower bound on set_bits

            int idx = lower_bound(set_bit.begin(), set_bit.end(), need) - set_bit.begin();

            // update the count

            count += set_bit.size() - idx;
        }

        return count;
    }
};
```

