class Solution {
public:
    int setbits(int n) {
        int cnt = 0;
        while(n) {
            cnt += n&1;
            n = n >> 1;
        }
        return cnt;
    }



    long long countExcellentPairs(vector<int>& nums, int k) {
	   // we are using set just to remove duplicates 
        unordered_set<int> set;
        for(int& i : nums) {
            set.insert(i);
        }
        nums.clear();
        for(auto&it: set) nums.push_back(it);
        int n = nums.size();
		// here i am not using any extra space for storing the setbits count for each value in the nums
        for(int i = 0; i < n ;i++) {
            nums[i] = setbits(nums[i]);
        }
		// sorting the function because of lower_bound function
		// if     nums[i] + any found number in nums >= k than we can definately say that 
		// all the number after this any found number in nums will also satisfy the condition
		// so apply lower_bound there
        sort(nums.begin(), nums.end());          
        long long ans = 0;
        for(int i = 0; i < n; i++) {
            ans += n-(lower_bound(nums.begin(), nums.end(), k-nums[i])-nums.begin());
        }
        return ans;
    }
};
