    {
        long long count = 0;
        while (n) {
            n &= (n - 1);
            count++;
        }

        return count;
    }

    long long countExcellentPairs(vector<int>& nums, int k) {

        sort(nums.begin(), nums.end());

        auto ip = unique(nums.begin(), nums.end());  
        nums.resize(distance(nums.begin(), ip));
		//  It removes all the adjacent similar elements from the vector giving us the distinct elements. (make sure to sort first).


        vector<int> vec;
        long long n = nums.size();
        for (long long i = 0 ; i < n ; i++){
            vec.push_back(countSetBits(nums[i])); // count set bits in each element.
        }

        sort(vec.begin(), vec.end()); 

        long long result = 0;
        n = vec.size();

        for (long long i = 0; i < vec.size() ; i++){
            long long val = vec[i];

            long long idx = lower_bound(vec.begin(), vec.end(), max(k - val, 0LL)) - vec.begin();  
			// max(k - val, 0LL))  to handle values that are greater than k hence total no. of pairs they can make is from index 0 to all values till n.

			result += n - idx;
			// adding total pairs that single value can make. 
        }

        return result;

    }
};
