class Solution {
public:
    long long countExcellentPairs(vector<int>& nums, int k) {
        long long cnt[30] = {};

        long long ans = 0;

        for(int it:unordered_set<int> (begin(nums),end(nums)))
		{
            ++cnt[__builtin_popcount(it)];
        }

        for(int i=1;i<30;i++){
            for(int j=1;j<30;j++)
            {
                if(i + j >= k)
                {
                    ans += cnt[i]*cnt[j];
                }
            }
        }
        return ans;
    }
};
```
