class Solution {
    public long countExcellentPairs(int[] nums, int k) {
        Set<Integer> set = new HashSet<>();
        for(int n: nums) set.add(n);

        List<Integer> list = new ArrayList<>();
        for(int n: set){
            list.add(getBitCount(n));
        }
        Collections.sort(list);
        long res = 0;
        for(int i = 0; i< list.size(); i++){
            int lo = i, hi = list.size()-1;
            long cur = list.size();
            while(lo <= hi){
                int md = lo + (hi - lo)/2;
                if(list.get(md) + list.get(i) >=k){
                    cur = md;
                    hi = md - 1;
                }else{
                    lo = md + 1;
                }
            }
            res += 2 * (list.size() - cur);
            if(i == cur) res -= 1;
        }
        return res;
    }

    private int getBitCount(int n){
        int count = 0;
        while(n!=0){
            count += (n&1);
            n>>=1;
        }
        return count;
    }
}

