class Solution {

    Map<Integer, Integer> setBitsDP = new HashMap<>();

    public long countExcellentPairs(int[] nums, int k) {

        /*
        countSetBits(a & b) = count set bits of a + count set bits of b - common set bits between a and b
        countSetBits(a | b) = common set bits between a and b
        countSetBits(a & b) + countSetBits(a | b) = countSetBits(a) + countSetBits(b)
        */
        Map<Integer, Set<Integer>> container = new HashMap<>();
        for (int num : nums) {
            int currCount = Integer.bitCount(num);
            container.putIfAbsent(currCount, new HashSet<>());
            container.get(currCount).add(num);
        }

        // since Integer is 32 bits
        long ans = 0;
        for (int i = 1; i < 32; i++) {
            for (int j = i; j < 32; j++) {
                if (container.containsKey(i) && container.containsKey(j) && (i + j) >= k) {
                    if (i == j) {
					    // count only once like (3, 3)
                        ans += (long)(container.get(i).size() * container.get(j).size());
                    } else {
                        // both ways permutation like (1, 2) and (2, 1)
                        ans += (long)(container.get(i).size() * container.get(j).size() * 2);
                    }
                }
            }
        }

        return ans;
    }
}
