class Solution {
  public long countExcellentPairs(int[] nums, int k) {
    int n = nums.length;

    HashSet<Integer> set = new HashSet<>();
    for (int i = n - 1; i >= 0; i--) set.add(nums[i]);

    n = set.size();
    int[] dist = new int[n];
    int i = 0;
    for (int next : set) dist[i++] = Integer.bitCount(next);

    Arrays.sort(dist);
    long ans = 0;

    for (i = 0; i < n; i++) {
      int ind = bsearch(dist, k - dist[i]);

      if (ind >= 0) ans += (n - ind + 0l);
    }

    return ans;
  }

  public int bsearch(int[] d, int t) {
    int l = 0;
    int r = d.length - 1;
    int ans = -1;
    while (l <= r) {
      int mid = (l + r) / 2;

      if (d[mid] < t)
        l = mid + 1;
      else {
        ans = mid;
        r = mid - 1;
      }
    }
    return ans;
  }
}
