class Solution:
    def countExcellentPairs(self, nums: List[int], k: int) -> int:
        nums = set(nums)
        freq = [0]*30
        for x in nums: freq[bin(x).count('1')] += 1
        prefix = list(accumulate(freq, initial=0))
        ans = 0 
        for x in nums: 
            bits = bin(x).count('1')
            lo = min(30, max(0, k-bits))
            ans += prefix[-1] - prefix[lo]
