        }
        return count;
    }

    long long countExcellentPairs(vector<int>& nums, int k) {
        ll ans=0;
        set<int>s;
        for(auto i:nums) //unique elements
        {
            s.insert(i);
        }

        vector<int>a; //to store the bitcounts
        for(auto it:s)
        {
            a.push_back(bitcount(it));
        }
        sort(a.begin(),a.end());

        int l=0;
        int r=a.size()-1;


        while(l<=r)//find the number of pairs
        {
            if(a[l]+a[r]<k)
            {
                l++;
            }
            else {
                ans+=2*(r-l)+1;
                r--;
                l=0;
            }
        }
