class Solution {
public:
    inline static int num_bits(int x){
        int res = 0;
        while(x){
            res += x & 1;
            x >>= 1;
        }
        return res;
    }

    long long countExcellentPairs(vector<int>& nums, int k) {
        sort(begin(nums), end(nums));
        nums.erase(unique(begin(nums), end(nums)), end(nums));

        vector<tuple<int, int>> bnums(nums.size());
        transform(begin(nums), end(nums), begin(bnums), [](auto x) { return make_tuple(x, num_bits(x)); });
        sort(begin(bnums), end(bnums), [](auto a, auto b){return get<1>(a) < get<1>(b);});

        long long res = 0;
        for(auto it = begin(bnums); it != end(bnums); ++it){
            if(2 * get<1>(*it) >= k){
                long long n = distance(it, end(bnums));
                return res + n * n;
            }
            auto it_start = lower_bound(next(it), end(bnums), k - get<1>(*it), [](auto a, auto b){return get<1>(a) < b;});
            res += distance(it_start, end(bnums)) * 2;
        }
        return res;
    }
};
```

