class Solution:
    def countExcellentPairs(self, nums: List[int], k: int) -> int:
        arr=[bin(num).count('1') for num in set(nums)]
        arr.sort()
        l, r, res = 0, len(arr) - 1, 0
        while l <= r:
            if arr[l] + arr[r] >= k:
                res += (r - l) * 2 + 1
                r -= 1
            else:
                l += 1
        return res
