class Solution {
public:
    long long countExcellentPairs(vector<int>& nums, int k) {
        set<int>st;
        for(int i=0;i<nums.size();i++){
            st.insert(nums[i]);
        }
        vector<int>noofbits;
        for(auto it:st){
            int val= __builtin_popcount(it);
        //__builtin_popcount(int num) is a function use for counting the set bit in an integer.
            noofbits.push_back(val);

        }
        sort(noofbits.begin(), noofbits.end());

        int n=noofbits.size();
        int l=0;
        int r=n-1;
        long long res=0;

        while(l<n && r>=0){
            if(noofbits[l]+noofbits[r]<k){
                l++;
            }else{
                res+=(n-l);
                r--;
            }
        }
        return res;
    }
};
```

