class Solution {
public:
    vector<vector<int>>v;
    unordered_set<int>st;
    long long countExcellentPairs(vector<int>& nums, int k) {
        // set bits of num1 or num2 + set bits of num1 & num2 = set bits
        // of num1 + set bits of num2
        // or will preserve the max(num1,num2) set bits
        // & will preserve the common set bits of num1 and num2
        // we will make a list which will store set bits of ele,ele
        // sort the list and apply binary search on it to find all
        // distinct pairs
        for(int i=0;i<nums.size();i++)
        {
            if(st.find(nums[i])==st.end())
            {
                v.push_back({bit_count(nums[i]),nums[i]});
                st.insert(nums[i]);
            }
        }
        sort(v.begin(),v.end());
        long long int ans=0;
        for(int i=0;i<v.size();i++)
        {
            ans=ans+fun(v[i][1],k);
        }
        return ans;
    }
    long long int fun(int &num,int &k)
    {
        int start=0;
        int end=v.size()-1;
        int mid;
        long long int ans=0;
        while(start<=end)
        {
            mid=(start+end)/2;
            if((bit_count(num)+v[mid][0])>=k)
            {
                ans=ans+(end-mid+1);
                end=mid-1;
            }
            else
            {
                start=mid+1;
            }
        }
        return ans;
    }
    int bit_count(int &n)
    {
        int temp=n;
        int ans=0;
        while(temp)
        {
            ans++;
            temp=temp-(temp & (-1*temp));
        }
        //cout<<ans<<endl;
        return ans;
    }
};
