class Solution {
public:
    int bit(int n)
    {
        int cnt=0;
        while(n!=0)
        {
            if(n%2!=0)
            cnt++;
            n=n/2;
        }
        return cnt;
    }

    long long countExcellentPairs(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> temp(n);
        unordered_set<int> s;
        unordered_map<int,int> mp;
        for(int i=0;i<n;i++)
        {
            temp[i]=bit(nums[i]);
        }
        for(int i=0;i<n;i++)
        {
            if(s.find(nums[i])==s.end())
            {
                s.insert(nums[i]);
                mp[temp[i]]++;
            }
        }
        unordered_set<int> s1;
        long long ans=0;
        for(int i=0;i<n;i++)
        {
            if(s1.find(nums[i])==s1.end())
            {
                s1.insert(nums[i]);
                int curr=temp[i];
                int req=k-curr;
                for(auto x:mp)
                {
                    if(x.first>=req)
                    {
                        ans+=x.second;
                    }
                }
            }
        }
        return ans;
    }
};
