        return __builtin_popcount(a) < __builtin_popcount(b);
    }
    long long countExcellentPairs(vector<int>& nums, int k) {
        set<int> st;
        for(auto &x : nums)st.insert(x);
        vector<int> v1;
        for(auto &x : st)v1.push_back(x);
        sort(v1.begin(),v1.end(),Sort);
        long long cnt = 0;
        for(int i=0;i<v1.size();i++)
        {
            int cnt1 = __builtin_popcount(v1[i]);
            int req = k-cnt1;
            int l=i, r = v1.size()-1, res = r+1;
            while(l <= r)
            {
                int mid = (l+r)/2;
                if(__builtin_popcount(v1[mid]) >= req)
                {
                    res = mid;
                    r = mid-1;
                }
                else l= mid+1;
            }
            cnt += (1LL*(v1.size()-res)*2);
            if(res == i)cnt--;
        }
        return cnt;
    }
};
