class Solution {
public:
    long long countExcellentPairs(vector<int>& arr, int k) {

        long long cnt=0;
        unordered_set<int> s;
        for(auto i:arr)s.insert(i);

        vector<int> v;
        for(auto& i: s)v.push_back(__builtin_popcount(i));

        sort(v.begin(),v.end());

        for(auto i:v){
            auto it = lower_bound(v.begin(),v.end(),k-i);
            cnt += (v.end()-it);
        }
        return cnt;
    }
};
