        sort(x.begin(),x.end());
        long long ans = 0;
        int n = x.size();
        for(int i=0;i<n;i++){
            auto it = x.begin()+i+1;
            int idx = lower_bound(it,x.end(),k-x[i])-x.begin();
            if(idx>=i){
                ans+= 1LL*(n-idx);
            }
        }
        ans*=2LL;
        // handles the cases where same element forms pair
        for(auto &val:x){
            if(2*val>=k) ans++;
        }

        return ans;
    }
};
