class Solution {
/*
	numOfSetBits(a & b) + numOfSetBits(a | b) == numOfSetBits(a) + numOfSetBits(b), because:
	a=0,b=0, a&b = 0, a|b = 0
	a=0,b=1, a&b = 0, a|b = 1
	a=1,b=1, a&b = 1, a|b = 1
*/
    public long countExcellentPairs(int[] nums, int k) {

        HashMap<Integer, HashSet<Integer>> map = new HashMap<>(); // (numOfSetBits, [numbers....])
		// time O(n)
        for (int i = 0; i < nums.length; i++){
            int n = numOfSetBits(nums[i]);
            if (!map.containsKey(n)) map.put(n, new HashSet<>());
            map.get(n).add(nums[i]);
        }

        long res = 0;

        // numOfSetBits(a & b) + numOfSetBits(a | b) == numOfSetBits(a) + numOfSetBits(b)
		// time O(1), because  0 <= i, j <= 32
        for (int i : map.keySet()) {
            for (int j : map.keySet()) {
                if (i + j >= k) {
                    int si = map.get(i).size();
                    int sj = map.get(j).size();
                    res += si * sj;
                }
            }
        }
        return res;
    }

    // time O(1)
    private int numOfSetBits(int n) {
        int res = 0;
        while (n > 0) {
            if ((n & 1) == 1) res++;
            n >>= 1;
        }
        return res;
    }
}
