func countExcellentPairs(nums []int, k int) int64 {
    uniques := make(map[int]bool)
    arr := make([]int, 32)

    for _, v := range nums {
        if _, ok := uniques[v]; !ok {
            uniques[v] = true
            arr[bits(v)]++
        }
    }

    acc := 0
    suffix := make([]int, 32)

    for i := 31; i >= 0; i-- {
        acc += arr[i]
        suffix[i] = acc
    }

    var ans int64 = 0

    for i := range arr {
        if(k - i >= 32) {
            continue
        }
        if(k - i < 1) {
            ans += int64(arr[i] * suffix[1])
        } else {
            ans += int64(arr[i] * suffix[k - i])
        }
    }

    return ans
}

func bits(num int) int {
    ans := 0
    for num > 0 {
        if num%2 != 0 {
            ans++
        }
        num /= 2
    }

    return ans
}
