class Solution {
    public long countExcellentPairs(int[] nums, int k) {
        int n = nums.length;
        int[] count = new int[32];  // count[i] denotes count of numbers with no. of set bits i.
        Set<Integer> unique = new HashSet<>();
        for(int num:nums){
            if(unique.contains(num)) continue;
            count[noOfSetBits(num)]++;
            unique.add(num);
        }
        for(int i=1;i<32;i++){
            count[i] += count[i-1];  // Prefix array calculation for count. 
        }
        // System.out.println(Arrays.toString(count));
        long ans = 0;
        for(int num:unique){
            int target = Math.max(1,k - noOfSetBits(num));
            if(target > 32){
                continue;
            }
            ans += count[31] - count[target-1];
        }

        return ans;
    }
    private int noOfSetBits(int n){
        int c = 0;
        while(n!=0){
            n = n&(n-1);
            c++;
        }
        return c;
    }
}
