def countExcellentPairs(self, nums, k):                                              # O(N): 1liner
    return sum(
        x * y
        for (i, x), (j, y) in itertools.product(collections.Counter(map(int.bit_count, set(nums))).items(), repeat=2)
        if i + j >= k
    )


def countExcellentPairs(self, nums, k):                                              # O(N)
    C = collections.Counter(map(int.bit_count, set(nums)))                           # O(N)
    return sum(C[i] * C[j] for i, j in itertools.product(C, repeat=2) if i + j >= k) # O(1): len(C) < 32
```

