def countExcellentPairs(self, nums, k):                                              # O(NlogN)
    A = sorted(map(int.bit_count, set(nums)))                                        # O(NlogN)
    return sum(len(A) - bisect.bisect_left(A, k - x) for x in A)                     # O(NlogN)
