long long countExcellentPairs(vector<int>&a, int k) {
        sort(a.begin(),a.end());
        auto itr=unique(a.begin(),a.end());
        a.resize(itr-a.begin());
        sort(a.begin(),a.end(),
             [](int x,int y){return __builtin_popcount(x)<__builtin_popcount(y);});
        long long  ans=0;
        for(int i=0;i<a.size();i++)
        {
            if(__builtin_popcount(a[i])>=k){ans++;ans+=2*((int)a.size()-i-1);continue;}
            if(2*__builtin_popcount(a[i])>=k)ans++;
            int l=i+1,r=(int)a.size()-1,target=k-__builtin_popcount(a[i]);
            if(target>__builtin_popcount(a.back()))continue;
            while(l<r)
            {
                int m=l+(r-l)/2;
                if(__builtin_popcount(a[m])>=target)
                    r=m;
                else l=m+1;
            }
            ans+=2*((int)a.size()-l);

        }
        return ans;
    }
