class Solution {
public:
    long long countExcellentPairs(vector<int>& nums, int k) {
        vector<int> count(33, 0), mp;
        unordered_map<int, int> is;
        long long res=0;
        for(auto num: nums)
        {
            if(is[num])
                continue;
            is[num]=1;
            int bits=0;
            for(int i=0; i<32; i++)
                if((1<<i)&num)
                    bits++;
            count[bits]++;
            mp.push_back(bits);
        }
        for(int i=31; i>=0; i--)
            count[i]+=count[i+1];
        for(int i=0; i<mp.size(); i++)
        {
            if(k-mp[i]<=0)
                res+=(long long)count[0];
            else if(k-mp[i]<=32)
                res+=(long long)count[k-mp[i]];
        }
        return res;
    }
};
