class Solution:
    def countExcellentPairs(self, nums: List[int], k: int) -> int:
        st = list(set(nums))

        pp = []
        for n in st: # O(n)
            pp.append(n.bit_count())

        pp.sort(reverse=True) # O(nlogn)

        i = 0
        j = len(pp) - 1

        ans = 0
        while i <= j: # O(n)
            while j >= i and pp[j] + pp[i] < k:
                j -=1

            if j >= i:
                ans += (2 * (j - i + 1) - 1)

            i +=1

        return ans
