class Solution {
    private int countSetBits(int n){
        int count = 0;
        while(n != 0){
            int rsbm = (n & (-n));
            n = (n - rsbm);
            count++;
        }
        return count;
    }

    private int binarySearch(List<Integer> al, int hi, int x){
        int lo = 0;
        int ans = -1;
        while(lo <= hi){
            int mid = lo + (hi - lo) / 2;

            if(al.get(mid) >= x){
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }

    public long countExcellentPairs(int[] nums, int k) {
        HashSet<Integer> set = new HashSet<>();
        for(int val : nums){
            set.add(val);
        }
        int [] arr = new int[set.size()];
        int c = 0;
        for(int val : set){
            // System.out.print(val + " ");
            arr[c++] = val;
        }

        List<Integer> list = new ArrayList<>();
        for(int val : arr){
            int setBits = countSetBits(val);
            list.add(setBits);
        }
        Collections.sort(list);
        long ans = 0;

        for(int i = 0; i < list.size(); i++){
            int val = list.get(i);
            if(val >= k){
                ans += i * 2 + 1;
            } else {
                int idx = binarySearch(list, i, k - val);
                if(idx != -1){
                    ans = ans + (i - idx) * 2 + 1;
                }
            }
        }
        return ans;
    }
}

