class Solution {
public:
    // comparetor for sorting based on no. of set bits
    bool static cmp(int &x, int &y){
        return __builtin_popcount(x) < __builtin_popcount(y);
    }

    long long countExcellentPairs(vector<int>& nums, int k) {
        sort(nums.begin(), nums.end());

        // unique removes consecutive duplicates(that's why first sorted the vector)
        auto itr2FirstDuplicate = unique(nums.begin(), nums.end());

        // resizing it to non duplicates
        nums.resize(distance(nums.begin(), itr2FirstDuplicate));

        // sorting based on no. of set bits
        sort(nums.begin(), nums.end(), cmp);


        // now problem reduced to count no of pairs with sum greater than or equal to given target 
        int n = nums.size();
        int i=0, j=n-1;

        // we don't take long long as expected then runtime error at 51/56
        long long count = 0;
        while(i <= j){
            if(__builtin_popcount(nums[i]) + __builtin_popcount(nums[j]) < k){
                i++;
            }
            else{
                // multiplied by 2 bcz (2,1) and (1,2) are distinct pairs
                count += (2*(j-i)+1);
                j--;
            }
        }
        return count;
    }
};
