class Solution {
	public long countExcellentPairs(int[] nums, int k) {
		java.util.Arrays.sort(nums);
		int[] c = new int[30];
		for (int i = 0, n = nums.length, w = 0; i < n; ++i) {
			int v = nums[i];
			if (w != v)
				++c[Integer.bitCount(w = v)];
		}
		long a = 0;
		for (int i = Math.max(k - 30, 1); i < 30; ++i) {
			long d = c[i];
			for (int j = Math.max(k - i, 1); j < 30; ++j)
				a += d * c[j];
		}
		return a;
	}
}

