public long countExcellentPairs(int[] nums, int k) {
	Set<Integer> s = new HashSet();
	int[] m = new int[k+1];

	for(int i:nums){
		if(!s.contains(i)){
			s.add(i);
			m[Math.min(k,Integer.bitCount(i))]++;            
		}
	}
	long result = 0;
	long sum = 0;
	for(int i = k; i>(k+1)/2; i--){
		sum += m[i];
		result+= sum*2*(m[i-1]+m[k-i])+((long)m[i])*m[i];
	}
	long j = m[(k+1)/2];
	result+=j*j;
	if((k&1)==1) {
	  sum+=j;
	  result+=sum*2*m[k/2];              
	}
	return result;
}
