```java
class Solution {
    public long countExcellentPairs(int[] nums, int k) {
        Map<Integer, Set<Integer>> map = new HashMap<>();
        for(int num : nums) {
            int setBitCnt = setBitCount(num);
            if(!map.containsKey(setBitCnt)) {
                map.put(setBitCnt, new HashSet<>());
            }
            map.get(setBitCnt).add(num);
        }
        long ans = 0;
        Set<Integer> vis = new HashSet<>();
        for(int num : nums) {
            if(vis.contains(num)) {
                continue;
            }
            int reqd = Math.max(0, k - setBitCount(num));
            for(int key : map.keySet()) {
                if(key >= reqd) {
                    ans += (long) map.get(key).size();
                }
            }
            vis.add(num);
        }
        return ans;
    }
    public int setBitCount(int num) {
        int cnt = 0;
        while(num != 0) {
            if((1 & num) == 1) {
                cnt++;
            }
            num = num >> 1;
        }
        return cnt;
    }
}

