class Solution {
    public long countExcellentPairs(int[] nums, int k) {
        long result = 0;
        Map<Integer, Set<Integer>> map = new HashMap<>();
        for (int i = 0; i <= 32; i++) {
            map.put(i, new HashSet<Integer>());
        }
        for (int i = 0; i < nums.length; i++) {
            int num = nums[i];
            int copyNum = num;
            int numberOfBits = 0;
            while (num > 0) {
                if (num % 2 != 0) {
                    numberOfBits++;
                }
                num = num / 2;
            }
            if (map.containsKey(numberOfBits)) {
                Set<Integer> set = map.get(numberOfBits);
                set.add(copyNum);
                map.put(numberOfBits, set);                
            } else {
                Set<Integer> set = new HashSet<>();
                set.add(copyNum);
                map.put(numberOfBits, set);
            }
        }
        for (int i = 0; i <= 32; i++) {
            for (int j = 0; j <= 32; j++) {
                if (i + j < k) {
                    continue;
                }
                result = result + map.get(i).size() * map.get(j).size();
            }
        }
        return result;
    }
}
