class Solution:
    def countExcellentPairs(self, nums: List[int], k: int) -> int:
        xs = set(nums)
        bs = []
        for v in xs:
            cnt = 0
            for i in range(32):
                if (v >> i) & 1 == 1:
                    cnt += 1
            bs.append(cnt)
        ans = 0
        bs.sort()
        # print(f"bs {bs}")
        n = len(bs)
        l, r = 0, n - 1


        while l <= r:
            if bs[l] + bs[r] >= k:
                ans += (r-l) * 2 + 1
                r -= 1
            else:
                l += 1

        return ans
