class Solution {
public:
  inline int CountOnes5 (unsigned int n ) {
    n -= (n>>1) & 0x55555555;
    n = ((n>>2) & 0x33333333 ) + (n & 0x33333333);
    n = ((((n>>4) + n) & 0x0F0F0F0F) * 0x01010101) >> 24;
    return n;
  } 

  vector<int> table; 

  void fill_table(vector<int>& nums){
    unordered_set<int> st;
    for(int n: nums)
      if(!st.count(n))
        st.insert(n), table[CountOnes5(n)]++;
  }

  long long countExcellentPairs(vector<int>& nums, int k) {
    if(k > 58) return 0;
    table.resize(30, 0);
    fill_table(nums);

    while(table.back() == 0) table.pop_back();
    if(table.size()*2 < k) return 0;

    vector<int> sum = table;
    sum.resize(max(31,k+1),0);
    for(int i = sum.size() - 2; i; i--) sum[i] += sum[i+1]; 

    long ans = 0, dop = 0;
    int i = 1, j = k-1, lim = (k+1)>>1;

    for(; i != lim; i++, j--) ans += (long)table[i] * sum[j];  

    for(; i != 30; i++) 
      if(table[i]) dop += (long)table[i] * table[i], ans += (long)table[i] * sum[i+1]; 

    return dop + (ans<<1);
  }
};
```

