class Solution {

    public long countExcellentPairs(int[] nums, int k) {
        Set<Integer> set = new HashSet<>();
        for(int num : nums){
            set.add(num);
        }
        List<Integer> list = new ArrayList<>();
        long ans = 0;
        for(int num : set){
            if( bitCount(num|num) + bitCount(num&num) >=k){
                //System.out.println(num+" ");
                ans++;
            }
            list.add(bitCount(num));
        }
        Collections.sort(list);
        //System.out.println(list);
        TreeMap<Long,Long> map = new TreeMap<>();
        for(int i = 0;i<list.size();i++){
            if(list.get(i)>=k){
                //System.out.println("Here");
                ans+=(2*(i));
            }else{
                Long first = map.ceilingKey(k-(long)list.get(i));
                //System.out.println("First "+first);
                if(first!=null){
                    Long idx = map.get(first);
                    long total  = (i-idx);
                    //System.out.println(total);
                    ans+=(2*total);
                }
            }
            if(!map.containsKey((long)list.get(i))){
                map.put((long)list.get(i),(long)i);
            }

        }
        //System.out.println(map);
        return ans;
    }
